    private void but20ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_but20ActionPerformed
        String s=txt2.getText();
        double num=0;
        String s3="";
        int i=0;
        double res;
        while(s.charAt(i)!=' ')
        {   s3+=s.charAt(i);
            i++;
        }
        num=Double.parseDouble(s3);
        i++;
        char op=s.charAt(i);
        String s2=txt1.getText();
        double num2=Double.parseDouble(s2);
        switch(op)
        {
            case '+':
                    res=num+num2;
                    txt2.setText(Double.toString(res));
                    break;
            case '-':
                    res=num-num2;
                    txt2.setText(Double.toString(res));
                    break;
            case '*':
                    res=num*num2;
                    txt2.setText(Double.toString(res));
                    break;
            case '/':
                    double res1=(double)num/num2;
                    txt2.setText(Double.toString(res1));
                    break;
        }
    }//GEN-LAST:event_but20ActionPerformed

